mapper.insert:
insert into orders (
    orders_code
  , customer_id
  , product_code
  , purchase_quantity
  , cart_state
) values (
    #{ordersCode}
  , #{customerId}
  , #{productCode}
  , #{purchaseQuantity}
  , 1
)

mapper.update:
update orders
set purchase_quantity = #{purchaseQuantity}
where orders_code = #{ordersCode}
  and customer_id = #{customerId}
  and product_code = #{productCode}
  and cart_state = 1

mapper.select:
select orders_code
from orders
where customer_id = #{customerId}
  and product_code = #{productCode}
  and orders_datetime is null
  and cart_state = 1

select purchase_quantity
from orders
where customer_id = #{customerId}
  and product_code = #{productCode}
  and orders_datetime is null
  and cart_state = 1

mapper.update:
update orders
set cart_state = 4
where orders_code = #{ordersCode}
  and customer_id = #{customerId}
  and product_code = #{productCode}
  and cart_state = 1
